package ch.bbw.zork;import java.util.HashMap;public class Room {    private String description;    private HashMap<String, Room> exits;    private String name;    public Room(String description, String name) {        this.description = description;        this.exits = new HashMap<>();        this.name = name;    }    public void setExits(Room norden, Room osten, Room sueden, Room westen) {        exits.put("norden", norden);        exits.put("osten", osten);        exits.put("sueden", sueden);        exits.put("westen", westen);    }    public String longDescription() {        StringBuilder stringBuilder = new StringBuilder(description + ".\n");        stringBuilder.append(exitString());        return stringBuilder.toString();    }    private String exitString() {        return "Richtungen: " + String.join(" ", exits.keySet());    }    public Room nextRoom(String direction) {        return exits.get(direction);    }    public String getName() {        return name;    }}